import compareword from './secret'

function rendermessage(guess){
    let message = ''
    if(guess.trim()){
      if(guess.length !== 5){
        message =  `${guess} was not a valid word`
      }
      else{
        let match = compareword(guess)
        if(match[0]){
          message = `<div>${guess} is the secret word!`
        }
        else{
          message = `<div>${guess} had ${match[1]} letters in common</div>`
        }
      }
    }

    return message
}

export default rendermessage;
