import UserInput from './UserInput'
import {useState} from 'react'
import compareword from './secret'

function App() {
  const [guess,setGuess] = useState('')
  let message = ''
  if(guess.trim()){
    message =  `Your guess: ${guess}`
    compareword(guess)
  }

  return (
    <div className="App">
        <UserInput onSubmit= {(word)=>setGuess(word)}/>
        {message}
    </div>
  );
}

export default App;
