  const [message,setMessage] = useState('')
  function alertMessage(word){
    setGuess(word);
  }

  return (
    <div className="App">
        <UserInput onSubmit= {(word)=>alertMessage(word)}/>
        <MessageBox type = {type} message = {message}/>
    </div>
  );
}

export default App;
