import {useState} from 'react'

const UserInput = function (){
    const [word,setWord] = useState('');
    const updateText = (e) => {setWord(e.target.value)};
    function result(word){
        //console.log(word)
        if(!word.trim()){
            return
        }
        console.log(word)
        setWord('')
    }
    return (
        <div className = 'input-panel'>
            <input onChange = {updateText} value = {word}/>
            <button onClick={()=>result(word)}>Submit
            </button>
        </div>
    )

}

export default UserInput
