import compareword from './secret'

function rendermessage(guess){
    let message = ''
    let type
    if(!guess.trim()){
        message =  `"${guess}" was not a valid word (No Empty Word)`
        type = 'warning'
    }
    else{
      if(guess.length !== 5){
        message =  `"${guess}" was not a valid word`
        type = 'warning'
      }
      else{
        let match = compareword(guess)
        if(match[0]){
          message = `"${guess}" is the secret word!`
          type = 'success'
        }
        else{
          message = `"${guess}" had ${match[1]} letters in common`
          type = 'info'
        }
      }
    }

    return {type,message}
}

export default rendermessage;
