import './App.css';
import {useState} from 'react'
import {Fetchcatlist} from "./servers";
import {FactCount} from "./FactCount";
import { Dropdown} from "./Dropdown";


function App() {

  // const {factsLoaded,setfactsLoaded} = useState(0)
  const [isLoading, setLoading] = useState(false)
  const [Facts,setFacts] = useState([])
  const [Size, setSize] = useState(5)

  const updateFacts = () =>{
    setLoading(true)
    Fetchcatlist()
    .then((facts)=>{
      setLoading(false)
      facts.map(key=>{
        Facts.push(key)
      })
      setFacts([...Facts])
    })
    .catch((err)=>{
      setLoading(false)
      console.log(err)
    })
  };

  return (
    <div className="App">
      <FactCount fact_array = {Facts}/>
      <div>
            <label htmlFor="cars">Choose a car:</label>
            <select name="cars" id="cars">
            <option value="volvo">Volvo</option>
            <option value="saab">Saab</option>
            <option value="mercedes">Mercedes</option>
            <option value="audi">Audi</option>
            </select>
        </div>
      {isLoading ? <div>Loading</div>: <button onClick={updateFacts}>Load Facts</button>}
    </div>
  );
}

export default App;
