import './App.css';
import {useState} from 'react'
import { Fetchcatlist } from "./servers";
import { FactCount } from "./FactCount";


function App() {

  // const {factsLoaded,setfactsLoaded} = useState(0)
  const [isLoading, setLoading] = useState(false)
  const [Facts,setFacts] = useState([])

  const updateFacts = () =>{
    setLoading(true)
    Fetchcatlist()
    .then((facts)=>{
      facts.map(key=>{
        console.log(key)
        setFacts([...Facts,key])
        console.log(Facts)
      })

      // setFacts([...Facts,1])
    })
    .catch((err)=>{
      setLoading(false)
      console.log(err)
    })
    // setFacts([...Facts, random ])
  };

  return (
    <div className="App">
      <FactCount fact_array = {Facts}/>
      <button onClick={updateFacts}>Load Facts</button>
      {/* {isLoading ? <div>Loading</div>:<button onClick={updateFacts}>Load Facts</button>} */}
    </div>
  );
}

export default App;
