import { useState,useEffect } from "react";
import {Fetchcatlist} from "./servers";
import { DisplayList } from "./DisplayList";

export const FactsList = ({setList,Size}) =>{

    const [locFacts,setlocFacts] = useState([])
    const [isLoading, setLoading] = useState(false)

    useEffect( () =>{
        setLoading(true)
        Fetchcatlist()
        .then((facts)=>{
          setLoading(false)
          setlocFacts(facts)
          setList(facts)
        })
        .catch((err)=>{
          setLoading(false)
          console.log(err)
        })
      },[setList]);



      return (
          <div>
            {isLoading ? <div>Loading data</div> : locFacts}
          </div>
      )

}
