import { useState,useEffect } from "react";
import {Fetchcatlist} from "./servers";
import { DisplayList } from "./DisplayList";
import { DisplayButton } from "./DisplayButton";
import logo from './spinner.gif'

export const FactsList = ({setList,maxLength}) =>{

    const [locFacts,setlocFacts] = useState([])
    const [isLoading, setLoading] = useState(false)
    const [startPage, setstartPage] = useState(0)

    useEffect( () =>{
        setLoading(true)
        Fetchcatlist()
        .then((facts)=>{
          setLoading(false)
          setlocFacts(facts)
          setList(facts)
        })
        .catch((err)=>{
          setLoading(false)
          console.log(err)
        })
      },[setList]);

      // const pageCount = parseInt(locFacts.length/maxLength) + (locFacts.length%maxLength?1:0)
      return (
          <div>
            {isLoading ? 
              <div>Loading data...
              <div>
                <img src={logo} alt="loading..." />
              </div>
              </div> : 
              <div>
                <DisplayList size={maxLength} facts = {locFacts} start = {startPage <0? 0:startPage} length = {locFacts.length}/>
                <DisplayButton startPage = {startPage <0? 0:startPage} maxLength={maxLength} setstartPage={setstartPage} facts = {locFacts} length = {locFacts.length}/>
              </div>
            }
          </div>
      )

}
