import { useState,useEffect } from "react";
import {Fetchcatlist} from "./servers";
import { DisplayList } from "./DisplayList";

export const FactsList = ({setList,maxLength}) =>{

    const [locFacts,setlocFacts] = useState([])
    const [isLoading, setLoading] = useState(false)
    const [startPage, setstartPage] = useState(0)

    useEffect( () =>{
        setLoading(true)
        Fetchcatlist()
        .then((facts)=>{
          setLoading(false)
          setlocFacts(facts)
          setList(facts)
        })
        .catch((err)=>{
          setLoading(false)
          console.log(err)
        })
      },[setList]);

      function pageDown (){
        setstartPage(startPage-maxLength)
        if(startPage <= 0){
          setstartPage(0)
        }
      }
      function pageUp(){
        setstartPage(startPage+maxLength)
        const len = locFacts.length
        if (startPage > len){

        }
      }

      const pageCount = ParseInt(locFacts.length/maxLength) + (locFacts.length%maxLength?1:0)

      return (
          <div>
            {isLoading ? 
              <div>Loading data</div> : 
              <div>
                <DisplayList size={maxLength} facts = {locFacts} start = {startPage}/>
                <button onClick = {pageDown}>&lt; Previous</button>
                {pageCount}
                <button onClick = {pageUp}>Next &gt;</button>
              </div>
            }
          </div>
      )

}
