import { useState,useEffect } from "react";
import {Fetchcatlist} from "./servers";

export const FactsList = () =>{

    const [Facts,setFacts] = useState([])
    // const [isLoading, setLoading] = useState(false)

    useEffect(  () =>{
        setLoading(true)
        Fetchcatlist()
        .then((facts)=>{
          setLoading(false)
          facts.forEach(element => {
            Facts.push(element)
          });
          setFacts([...Facts])
        })
        .catch((err)=>{
          setLoading(false)
          console.log(err)
        })
      },[]);


      return (
          {Facts}
      )

}
