export const DisplayList = ({start,size,facts}) =>{
    let loc_start = start
    let loc_end = start+size

    if (loc_start < 0){
        loc_start = 0
    }
    if(loc_end > facts.length){
        loc_end = facts.length
    }
    const formattedFacts = facts.slice(loc_start,loc_start+size).map(fact => 
            (<li key= {facts.indexOf(fact)} data-index={facts.indexOf(fact)}>{facts.indexOf(fact)+1}<br/>{fact}</li>)
        );

    function ShowingFacts(){
        let result = `${loc_start+1}-${loc_start+size}`
        if(loc_start+size > facts.length){
            result = `${loc_start+1}-${facts.length}`
        }
        return result
    }

    //console.log(formattedFacts)size={Size} facts = {locFacts} start = {startPage}
    return (
        <ul>
            Showing Facts: {ShowingFacts()}
            {formattedFacts}
        </ul>
    )
}
